from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left_2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right_2 = Motor(Port.B, Direction.CLOCKWISE)
Arm = Motor(Port.C, Direction.COUNTERCLOCKWISE)
drive_base_2 = DriveBase(left_2, right_2, 88, 71.3)


# The main program starts here.
drive_base_2.use_gyro(False)
drive_base_2.use_gyro(True)
drive_base_2.straight(-50)
Arm.run_target(500, -10)
drive_base_2.turn(-90)
drive_base_2.straight(-50)
drive_base_2.turn(360)
drive_base_2.turn(-360)
